export const getCurrentLocation = (updateParkingLatLng , updateShowParkingAddressModal)=>{
    function success(position) {
        const latitude  = position.coords.latitude;
        const longitude = position.coords.longitude;
        updateParkingLatLng( { lat:latitude, lng:longitude} );
    }

    function error() {
        alert('Unable to retrieve your location');
        updateShowParkingAddressModal(true);
    }

    if(!navigator.geolocation) {
        alert('Geolocation is not supported by your browser');
        } else {
        navigator.geolocation.getCurrentPosition(success, error);
    }


}

export const getParkingLots = (updateParkingLatLng , updateShowParkingAddressModal , setShowFindParking)=>{{

    function success(position) {
        const latitude  = position.coords.latitude;
        const longitude = position.coords.longitude;
        updateParkingLatLng( { lat:latitude, lng:longitude} );
        setShowFindParking(`https://www.google.com/maps/embed/v1/search?key=AIzaSyCNKfsXeTiMfS66RSVSMuYv5BEQVw5ohbI&zoom=16&q=parking near ${latitude},${longitude}`);

    }

    function error() {
        alert('Unable to retrieve your location');
        updateShowParkingAddressModal(true);
    }

    if(!navigator.geolocation) {
        alert('Geolocation is not supported by your browser');
        } else {
        navigator.geolocation.getCurrentPosition(success, error);
    }




}}
