export const getCurrentLocation = (updateParkingLatLng , updateShowParkingAddressModal, tempWhatToShow, setWhatToShow , parkingLatLng , setMainBtnText)=>{
    function success(position) {
        const latitude  = position.coords.latitude;
        const longitude = position.coords.longitude;
        updateParkingLatLng( { lat:latitude, lng:longitude} );
        if (tempWhatToShow === "parkingLocation"){
            setWhatToShow(`https://www.google.com/maps/embed/v1/place?key=AIzaSyCNKfsXeTiMfS66RSVSMuYv5BEQVw5ohbI&q=${latitude},${longitude}`);
            setMainBtnText("Get directions");
        }
        else if (tempWhatToShow === "currentLocation"){
            setWhatToShow(`https://www.google.com/maps/embed/v1/directions?key=AIzaSyCNKfsXeTiMfS66RSVSMuYv5BEQVw5ohbI&origin=${latitude},${longitude}&destination=${parkingLatLng.lat},${parkingLatLng.lng}&mode=walking`);
        }
        else if (tempWhatToShow === "findParkingLot"){
            setWhatToShow(`https://www.google.com/maps/embed/v1/search?key=AIzaSyCNKfsXeTiMfS66RSVSMuYv5BEQVw5ohbI&zoom=16&q=parking in -34.397, 150.644`);
        }
    }

    function error() {
        alert('Unable to retrieve your location');
        updateShowParkingAddressModal(true);

    }

    if(!navigator.geolocation) {
        alert('Geolocation is not supported by your browser');
        } else {
        navigator.geolocation.getCurrentPosition(success, error);
    }


}
