import axios from "axios";

export const getCurrentWeatherByCityName = ()=>{

    function success(position) {
        const latitude  = position.coords.latitude;
        const longitude = position.coords.longitude;
        axios.get(`http://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=586508fb2989d9e659ae350d284cee51`).then((res)=>{
            debugger   
        console.log("Success getCurrentWeatherByCityName : " + res);

        }).catch((error)=>{
            console.log("Error getCurrentWeatherByCityName : " + error);
        });

    }

    function error() {
        console.log("Error getting location")
    }




    navigator.geolocation.getCurrentPosition(success, error);


}
